import React, { Component } from "react";
import { Formik } from "formik";
import * as yup from "yup";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";

class Signup extends Component {
  state = {
    email: "",
    username: "",
    password: "",
    repassword: "",
  };

  handleSubmit = (values, { setSubmitting }) => {
    console.log(values);
  };

  render() {
    return (
      <div
        className="auth-layout-wrap"
      // style={{
      //   backgroundImage: "url(/assets/images/photo-wide-4.jpg)",
      // }}
      >
        <div className="auth-content">
          <div className="card o-hidden">
            <div className="row">


              <div className="">
                <div className="p-4">
                  <h4 className="mb-3 text-center text-24">Register Account</h4>
                  <div className="text-center text-10">
                    <span >Sign up for an free account now!
                    </span>
                    <br />
                    <span>
                      Fill in the details of the first organization you will
                    </span>
                    <br />
                    <span>be managing to get started.</span>
                  </div>
                  <Formik
                    initialValues={this.state}
                    validationSchema={SignupSchema}
                    onSubmit={this.handleSubmit}
                  >
                    {({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                    }) => (
                      <form onSubmit={handleSubmit} className="row">
                        <div className=" col-lg-6">
                          <div className="form-group">
                            <label htmlFor="username">Your name</label>
                            <input
                              className="form-control form-control-rounded"
                              name="username"
                              type="text"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.username}
                            />
                            {errors.username && touched.username && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.username}
                              </div>
                            )}
                          </div>
                          <div className="form-group">
                            <label htmlFor="email">Email address</label>
                            <input
                              name="email"
                              className="form-control form-control-rounded"
                              type="email"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.email}
                            />
                            {errors.email && touched.email && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.email}
                              </div>
                            )}
                          </div>
                          <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                              name="password"
                              className="form-control form-control-rounded"
                              type="password"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.password}
                            />
                            {errors.password && touched.password && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.password}
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="col-lg-6">
                          <div className="form-group">
                            <label htmlFor="username">Your name</label>
                            <input
                              className="form-control form-control-rounded"
                              name="username"
                              type="text"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.username}
                            />
                            {errors.username && touched.username && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.username}
                              </div>
                            )}
                          </div>
                          <div className="form-group">
                            <label htmlFor="email">Email address</label>
                            <input
                              name="email"
                              className="form-control form-control-rounded"
                              type="email"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.email}
                            />
                            {errors.email && touched.email && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.email}
                              </div>
                            )}
                          </div>
                          <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                              name="password"
                              className="form-control form-control-rounded"
                              type="password"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.password}
                            />
                            {errors.password && touched.password && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.password}
                              </div>
                            )}
                          </div>
                        </div>

                        <button
                          className="btn btn-primary w-48 my-1 btn-rounded "
                          // className="btn btn-rounded btn-primary w-48 my-1 mt-2 col-6 mx-auto"

                          type="submit"
                        >
                          Sign Up
                        </button>
                      </form>
                    )}
                  </Formik>
                  <div className="text-center">
                    <span>Already have an Account?</span>
                    <span>Login</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div >
    );
  }
}

const SignupSchema = yup.object().shape({
  username: yup.string().required("email is required"),
  email: yup.string().email("Invalid email").required("email is required"),
  password: yup
    .string()
    .min(8, "Password must be 8 character long")
    .required("password is required"),
  repassword: yup
    .string()
    .required("repeat password")
    .oneOf([yup.ref("password")], "Passwords must match"),
});

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps, {})(Signup);
