import React, { Component } from "react";
import { Formik } from "formik";
import * as yup from "yup";
import { loginWithEmailAndPassword } from "app/redux/actions/LoginActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";

const SigninSchema = yup.object().shape({
  email: yup.string().email("Invalid email").required("email is required"),
  password: yup
    .string()
    .min(8, "Password must be 8 character long")
    .required("password is required"),
});

class Signin extends Component {
  state = {
    email: "watson@example.com",
    password: "12345678",
  };

  handleChange = (event) => {
    event.persist();
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (value, { isSubmitting }) => {
    this.props.loginWithEmailAndPassword(value);
  };

  render() {
    return (
      <div
        className="auth-layout-wrap"style={{backgroundColor:'#f5f5f5'}}
      // style={{
      //   backgroundImage: "url(/assets/images/photo-wide-4.jpg)",
      // }}
      >
        <div className="auth-content-signin" >
          <div  className="
          /*card*/
           o-hidden">
            <div className="row">
              <div className="col-md-12">
                <div className="p-4">
                  <div className="auth-logo text-center mb-2">
                    <img src="/assets/images/logoKelick.png" alt="" />
                  </div>
                  <h3 className="mb-3 text-18 text-center">Welcome back!</h3>
                  <Formik
                    initialValues={this.state}
                    validationSchema={SigninSchema}
                    onSubmit={this.handleSubmit}
                  >
                    {({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                    }) => (

                      <form onSubmit={handleSubmit}>
                        <div className="row">
                          <input type='checkbox' className="text-center col-lg-3" />Employee Login
                          <input type='checkbox' className="text-center col-lg-3" />Admin Login

                        </div>
                        <div className="form-group ">
                          <label htmlFor="email">Organization ID</label>
                          <input
                              style={{backgroundColor:"#E5E5E5"}}
                            className="form-control   position-relative"
                            type="email"
                            name="email"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.email}
                          />
                          {errors.email && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.email}
                            </div>
                          )}
                        </div>
                        <div className="form-group">
                          <label htmlFor="password">Email ID</label>
                          <input
                          style={{backgroundColor:"#E5E5E5"}}
                            className="form-control "
                            type="password"
                            name="password"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.password}
                          />
                          {errors.password && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.password}
                            </div>
                          )}
                        </div>
                        <div className="form-group">
                          <label htmlFor="password">Password</label>
                          <input
                          style={{backgroundColor:"#E5E5E5"}}
                            className="form-control "
                            type="password"
                            name="password"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.password}
                          />
                          {errors.password && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.password}
                            </div>
                          )}
                        </div>
                        <div className="row">
                          <input type='checkbox' className="text-center col-lg-1" />
                          <span className="col-lg-2">sds</span>
                          <p className="col-lg-8">Forgot password?</p>
                        </div>
                        <button
                          className="btn-rounded btn-primary w-100 my-1 mt-2z"
                          type="submit" style={{backgroundColor: '#00CDC2'}}
                        >
                          Sign In
                        </button>

                      </form>
                    )}
                  </Formik>

                  <div className="mt-3 text-center">
                    <Link to="/session/forgot-password" className="text-muted">
                      <u>Sign up new company account</u>
                    </Link>
                  </div>
                </div>
              </div>
              <div
                className="col-md-6 text-center "
              // style={{
              //   backgroundSize: "cover",
              //   backgroundImage: "url(/assets/images/photo-long-3.jpg)",
              // }}
              >

              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  loginWithEmailAndPassword: PropTypes.func.isRequired,
  user: state.user,
});

export default connect(mapStateToProps, {
  loginWithEmailAndPassword,
})(Signin);
