import React, { Component } from "react";
import { Breadcrumb } from "@gull";
import { Button, Popover, OverlayTrigger } from "react-bootstrap";
import SimpleCard from "@gull/components/cards/SimpleCard";

// const popover = (props) => (
//     <Popover id="popover-basic" {...props}>
//         <Popover.Header as="h3">Popover right</Popover.Header>
//         <Popover.Body>
//             And here's some <strong>amazing</strong> content. It's very engaging.
//             right?
//         </Popover.Body>
//     </Popover>
// );

class viewNotification extends Component {
    state = {};
    render() {
        
        return (
            <div>
                {/* <Breadcrumb
                    routeSegments={[
                        { name: "UI Kits", path: "/uikits" },
                        { name: "Popover" },
                    ]}
                ></Breadcrumb> */}

                <div className="row">
                    <div className="col-md-12">
                        <SimpleCard title="Organization" className="mb-4 ">
                            <div className='row '>
                                <div className="col-9">
                                    <p>
                                        A new organization Asure Pro has been created.
                                    </p>
                                </div>
                                <div className="col-3">
                                    <OverlayTrigger
                                        trigger="click"
                                        placement="right"
                                    // overlay={popover}
                                    >
                                        <Button className="text-white rounded-3">
                                            View Organization Info
                                        </Button>
                                    </OverlayTrigger>
                                </div>
                            </div>
                        </SimpleCard>
                    </div>
                    <div className="d-flex justify-content-end">
                        <Button className="mx-2" variant="danger" onClick={firstStep}>
                            Cancel
                        </Button>
                        <Button
                            disabled={currentStep === 1}
                            className="mx-1"
                            variant="info"
                            onClick={previousStep}
                        >
                            Previous
                        </Button>
                        <Button
                            disabled={currentStep === totalSteps}
                            className="mx-1"
                            variant="info"
                            onClick={nextStep}
                        >
                            Next
                        </Button>
                        <Button
                            disabled={false}
                            className="mx-1"
                            variant="primary"
                            onClick={lastStep}
                        >
                            Finish
                        </Button>
                    </div>

                    {/* <div className="col-md-12">
                        <SimpleCard title="four directions" className="mb-4">


                            {["top", "right", "bottom", "left", "auto"].map((dir) => (
                                <OverlayTrigger
                                    key={dir}
                                    trigger="click"
                                    placement={dir}
                                    overlay={popover}
                                >
                                    <Button className="me-2">Popover on {dir}</Button>
                                </OverlayTrigger>
                            ))}
                        </SimpleCard>
                    </div> */}


                </div>
            </div>
        );
    }
}

export default viewNotification;
