// import React, { Component } from "react";
// import { Formik } from "formik";
// import * as yup from "yup";
// import { connect } from "react-redux";
// import { Button } from "react-bootstrap";
// import { Link } from "react-router-dom";

import React, { Component } from "react";
import { Breadcrumb } from "@gull";
import { Row, Col, Tab, Tabs, Nav } from "react-bootstrap";
import SimpleCard from "@gull/components/cards/SimpleCard";

const customTabHeader = (title, icon) => (
    <div className="d-flex align-items-center">
        <span className="me-2">
            <i className={icon}></i>
        </span>
        <span>{title}</span>
    </div>
);

class AppTab extends Component {
    state = {};
    randomText =
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.";
    render() {
        return (
            <div>

                <div className="row  text-center">

                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                                Organization
                                Basic Info

                            </div>
                        </div>
                    </div>

                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                            Organization
                                Basic Info
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center  p-3  ">
                                Branches/Outlets
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                                Announcements
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                                Working Hours
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                                Holiday Work List
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-1 w-4 mx-4 w-30 mr-15 col-md-12 col-sm-12">
                        <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                            <div className="card-body text-center row d-inline-flex p-3  ">
                                Manage
                                Asst. Admin
                            </div>
                        </div>
                    </div>

                </div>

                <div>
                <Formik
                    initialValues={this.state}
                    validationSchema={SignupSchema}
                    onSubmit={this.handleSubmit}
                  >
                    {({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                    }) => (
                      <form onSubmit={handleSubmit} className="row">
                        <div className=" col-lg-6">
                          <div className="form-group mb-3">
                            <label htmlFor="username" className="fw-bold">Email ID</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              className="form-control"
                              name="username"
                              type="text"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.username}
                            />
                            {errors.username && touched.username && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.username}
                              </div>
                            )}
                          </div>
                          <div className="form-group mb-3">
                            <label htmlFor="email" className="fw-bold">Phone No.</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              name="email"
                              className="form-control style={{color:#E5E5E5}}"
                              type="email"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.email}
                            />
                            {errors.email && touched.email && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.email}
                              </div>
                            )}
                          </div>
                          <div className="form-group mb-3">
                            <label htmlFor="password" className="fw-bold">Password</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              name="password"
                              className="form-control style={{color:#E5E5E5}}"
                              type="password"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.password}
                            />
                            {errors.password && touched.password && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.password}
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="col-lg-6">
                          <div className="form-group mb-3">
                            <label htmlFor="username" className="fw-bold">Organization Name</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              className="form-control style={{color:#E5E5E5}}"
                              name="username"
                              type="text"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.username}
                            />
                            {errors.username && touched.username && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.username}
                              </div>
                            )}
                          </div>
                          <div className="form-group mb-3">
                            <label htmlFor="email" className="fw-bold">Organization ID</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              name="email"
                              className="form-control style={{color:#E5E5E5}}"
                              type="email"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.email}
                            />
                            {errors.email && touched.email && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.email}
                              </div>
                            )}
                          </div>
                          <div className="form-group mb-3">
                            <label htmlFor="password" className="fw-bold">Organisation’s Country based in</label>
                            <input style={{ backgroundColor: '#E5E5E5' }}
                              name="password"
                              className="form-control style={{color:#E5E5E5}}"
                              type="password"
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.password}
                            />
                            {errors.password && touched.password && (
                              <div className="text-danger mt-1 ml-2">
                                {errors.password}
                              </div>
                            )}
                          </div>
                        </div>

                        <button
                          className="btn btn-primary border-0 w-50 my-1 mx-auto btn-rounded mt-3 mb-3 "
                          type="submit"
                          style={{ backgroundColor: ' #00CDC2' }}
                        >
                          Sign Up
                        </button>
                      </form>
                    )}
                  </Formik>
                </div>





            </div>
        );
    }
}

export default AppTab;
