                          <label htmlFor="firstName">First name</label>
                          <input
                            id="firstName"
                            name="firstName"
                            className="form-control"
                            placeholder="Enter your first name"
                            type="text"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.firstName}
                          />
                          {errors.firstName && touched.firstName && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.firstName}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="lastName">Last name</label>
                          <input
                            id="lastName"
                            name="lastName"
                            className="form-control"
                            placeholder="Enter your last name"
                            type="text"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.lastName}
                          />
                          {errors.lastName && touched.lastName && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.lastName}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="email">Email address</label>
                          <input
                            name="email"
                            id="email"
                            className="form-control"
                            placeholder="xox@xmail.com"
                            type="email"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.email}
                          />
                          {errors.email && touched.email && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.email}
                            </div>
                          )}
                          <small
                            id="emailHelp"
                            className="form-text text-muted"
                          >
                            We'll never share your email with anyone else.
                          </small>
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="phone">Phone</label>
                          <input
                            className="form-control"
                            id="phone"
                            name="phone"
                            placeholder="Enter phone"
                            type="text"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.phone}
                          />
                          {errors.phone && touched.phone && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.phone}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="cardNumber">Cradit card number</label>
                          <input
                            className="form-control"
                            id="cardNumber"
                            name="cardNumber"
                            placeholder="4444-4444-4444-4444"
                            type="number"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.cardNumber}
                          />
                          {errors.cardNumber && touched.cardNumber && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.cardNumber}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="website">Website</label>
                          <input
                            className="form-control"
                            id="website"
                            name="website"
                            placeholder="Web address"
                            onChange={handleChange}
                            onBlur={handleBlur}
                          />
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="picker2">Birth date</label>
                          <DateTime
                            name="birthDay"
                            onChange={date => {
                              handleChange({
                                target: {
                                  name: "birthDay",
                                  value: date.toDate()
                                }
                              });
                            }}
                          />
                          {errors.birthDay && touched.birthDay && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.birthDay}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <label htmlFor="picker1">Select</label>
                          <select
                            id="picker1"
                            className="form-control"
                            name="select"
                            value={values.select}
                            onChange={handleChange}
                            onBlur={handleBlur}
                          >
                            <option value="opt1">Option 1</option>
                            <option value="opt2">Option 2</option>
                            <option value="opt3">Option 3</option>
                          </select>
                          {errors.select && touched.select && (
                            <div className="text-danger mt-1 ml-2">
                              {errors.select}
                            </div>
                          )}
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <Form.Check
                            name="switch"
                            onChange={handleChange}
                            value={values.switch}
                            type="switch"
                            id="custom-switch"
                            label="I accept the terms & conditions"
                          />
                          <Form.Check
                            name="checkbox1"
                            onChange={handleChange}
                            value="check123"
                            checked={
                              values.checkbox1 &&
                              values.checkbox1.includes("check123")
                            }
                            type="checkbox"
                            id="check1"
                            label="I accept the policies"
                          />
                          <Form.Check
                            name="checkbox2"
                            onChange={handleChange}
                            value="check321"
                            checked={
                              values.checkbox2 &&
                              values.checkbox2.includes("check321")
                            }
                            type="checkbox"
                            id="check2"
                            label="Subscribe for future news letters"
                          />
                        </div>

                        <div className="col-md-6 form-group mb-3">
                          <fieldset onChange={handleChange}>
                            <Form.Group>
                              <Form.Label as="legend">
                                Choose any option
                              </Form.Label>
                              <Form.Check
                                type="radio"
                                label="first radio"
                                name="radio"
                                id="formHorizontalRadios1"
                                value="radio1"
                              />
                              <Form.Check
                                type="radio"
                                label="second radio"
                                name="radio"
                                id="formHorizontalRadios2"
                                value="radio2"
                              />
                              <Form.Check
                                type="radio"
                                label="third radio"
                                name="radio"
                                id="formHorizontalRadios3"
                                value="radio3"
                              />
                            </Form.Group>
                          </fieldset>
                        </div>

                        <div className="col-md-12 form-group mb-3">
                          <label htmlFor="picker2">Birth date</label>
                          <DateRange
                            startDate={new Date(
                              this.state.range.startDate
                            ).toLocaleDateString("en-GB")}
                            endDate={new Date(
                              this.state.range.endDate
                            ).toLocaleDateString("en-GB")}
                            onChange={this.handleDateRangeChange}
                          />
                        </div>
                        <div className="col-md-12">
                          <Button type="submit">Submit</Button>
                        </div>
                      </div>
                    </form>
                  )}
                </Formik>

                  <div className="mt-3 text-center">
                    <Link to="/session/forgot-password" className="text-muted">
                      <u>Sign up new company account</u>
                    </Link>
                  </div>
                </div>
              </div>
              <div
                className="col-md-6 text-center "
              // style={{
              //   backgroundSize: "cover",
              //   backgroundImage: "url(/assets/images/photo-long-3.jpg)",
              // }}
              >

              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  loginWithEmailAndPassword: PropTypes.func.isRequired,
  user: state.user,
});

export default connect(mapStateToProps, {
  loginWithEmailAndPassword,
})(Signin);
